     // padding:2,
  
    //  justifyContent: 'center',
  
    //  marginBottom:10 
    },
    bcvi:{
       justifyContent: 'center',width:"100%",height:"100%", alignItems: 'center',
    },
    bar:{
     
     
     // alignItems: 'center',
     backgroundColor :"#fff",
   color:"#fff"
     // marginVertical:5
    },
   vv:{
    marginTop:20,
    marginLeft:10,
    
   },
   scvert:{
    flexDirection:'row',
  
    
   },

   vert:{
    marginTop:30,

    flexDirection:'row',
   },
   vert1:{
    //marginTop:30,\
    marginLeft:9,

    flexDirection:'row',
   },
   
     Btn1:{
     
      width:350, 
     // height:'100%',
      alignItems: 'center',
    //  height:70,
      borderRadius:10,
      backgroundColor:"#ffff",
      padding:10,
      marginVertical:5
    
      
    },
    ora:{
     
      marginLeft:60,
      marginRight:50,
      padding:10,
      alignItems: 'center',
    },
    med:{
    marginLeft:9,
    alignItems: 'center',
    width:"31%"
    },
    Btn2:{
      width:60, 
      height:60,
      alignItems: 'center',
    //  height:70,
      borderRadius:10,
      backgroundColor:"#ffff",
      padding:10,
      
     
    
      
    },
    deleteadd:{
      marginRight:5

    },
    Btn:{
      width:65, 
      height:65,
      alignItems: 'center',
    //  height:70,
      borderRadius:10,
      backgroundColor:"#ffff",
      padding:10,
      marginVertical:5
    
      
    },
    varsta:{
      fontWeight: "bold",
       // width:350,
     fontSize:12,
     alignItems: 'center',
     // padding:2,

    },
    diagnostic:{
      fontWeight: "bold",
       // width:350,
     fontSize:20,
     alignItems: 'center',

    },
    scrol:{
      marginBottom:310
    }
  })
